import * as React from "react";
import {
  Button,
  Dialog,
  DialogActions,
  Box,
  Grid,
  DialogContent,
  Slide,
  AppBar,
  Toolbar,
  IconButton,
  Typography,
} from "@mui/material";

import DialogTitle from "@mui/material/DialogTitle";
import ClientFormField from "./ClientFormField";

const ClientDetailTabBarInformation = () => {
  const editFormButtons = {
    position: "absolute",
    bottom: "0",
    right: "10px",
    background: "#fafafa",
    width: "100%",
  };
  return (
    <>
      <DialogTitle sx={{ display: "flex", justifyContent: "space-between" }}>
        Client Information
        <Button onClick={handleEdit} variant="outlined">
          Edit
        </Button>
      </DialogTitle>
      <DialogContent>
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
          <ClientFormField client={client} disabled={isDisabled} />

          <DialogActions sx={editFormButtons}>
            {isDisabled ? null : (
              <>
                <Button onClick={handleEdit}>Cancel</Button>
                <Button type="submit">Submit</Button>
              </>
            )}
          </DialogActions>
        </Box>
      </DialogContent>
    </>
  );
};

export default ClientDetailTabBarInformation;
