import * as React from "react";
import {
  Button,
  Dialog,
  DialogActions,
  Box,
  Grid,
  DialogContent,
  Slide,
  AppBar,
  Toolbar,
  IconButton,
  Typography,
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import DialogTitle from "@mui/material/DialogTitle";
import ClientFormField from "./ClientFormField";
import PotentialClientTrackerApi from "../api";
import AlertBanner from "../AlertBanner";

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

export default function ClientDetail({ open, handleCancel, client }) {
  const [isDisabled, setIsDisabled] = React.useState(true);
  const [err, setErr] = React.useState(null);

  const handleEdit = () => {
    setIsDisabled(!isDisabled);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    const editClientFormData = {
      firstName: data.get("firstName"),
      lastName: data.get("lastName"),
      email: data.get("email"),
      phone: data.get("phone"),
      address: data.get("address"),
      citizenship: data.get("citizenship"),
      status: data.get("status"),
      channel: data.get("channel"),
      physician: data.get("physician"),
      diagnosis: data.get("diagnosis"),
      note: data.get("note"),
      daycare: data.get("daycare") === "true" ? true : false,
      medicaid: data.get("medicaid") === "true" ? true : false,
      isEnrolled: data.get("isEnrolled") === "true" ? true : false,
    };
    console.log(editClientFormData);
    try {
      await PotentialClientTrackerApi.updateClient(
        client.id,
        editClientFormData
      );
      window.location.reload(false);
    } catch (e) {
      setErr(e.response.data.error.message);
    }
  };

  const editFormButtons = {
    position: "absolute",
    bottom: "0",
    right: "10px",
    background: "#fafafa",
    width: "100%",
  };
  return (
    <Dialog
      fullScreen
      open={open}
      onClose={handleCancel}
      TransitionComponent={Transition}
      // maxWidth={"md"}
      // fullWidth
    >
      <AppBar sx={{ position: "relative" }}>
        <Toolbar>
          <Typography sx={{ ml: 2, flex: 1 }} variant="h6" component="div">
            Sound
          </Typography>
          <IconButton
            edge="start"
            color="inherit"
            onClick={handleCancel}
            aria-label="close"
          >
            <CloseIcon />
          </IconButton>
        </Toolbar>
      </AppBar>
      {err ? (
        <AlertBanner
          severity="error"
          title="Update Client Information Failed"
          msg={`${err} - please try again`}
        />
      ) : null}
      <DialogTitle sx={{ display: "flex", justifyContent: "space-between" }}>
        Client Information
        <Button onClick={handleEdit} variant="outlined">
          Edit
        </Button>
      </DialogTitle>
      <DialogContent>
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
          <ClientFormField
            client={client}
            disabled={isDisabled}
            sx={{ marginBottom: 3 }}
          />

          <DialogActions sx={editFormButtons}>
            {isDisabled ? null : (
              <>
                <Button onClick={handleEdit}>Cancel</Button>
                <Button type="submit">Submit</Button>
              </>
            )}
          </DialogActions>
        </Box>
      </DialogContent>
    </Dialog>
  );
}
