import * as React from "react";
import "./progress.css";
import { status } from "../../../helper/formSelectOptions";
import { DialogTitle, DialogContent, Chip, Divider } from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import moment from "moment";
import $ from "jquery";
import PotentialClientTrackerApi from "../../../api";
import ClientDetailTabBarProgressAddDatesForm from "./ClientDetailTabBarProgressAddDatesForm";
import ClientDetailTabBarProgressDatesMap from "./ClientDetailTabBarProgressDatesMap";

const ClientDetailTabBarProgressDates = ({
  statusMapping,
  client,
  updateClient,
}) => {
  return (
    <>
      <DialogTitle sx={{ fontSize: "1.5rem", marginTop: "15px" }}>
        Client Progress
      </DialogTitle>
      <DialogContent>
        <div className="deal-view">
          <div className="stepper">
            {status.map((s) => (
              <ClientDetailTabBarProgressDatesMap
                key={s.id}
                s={s}
                client={client}
                updateClient={updateClient}
                statusMapping={statusMapping}
              />
            ))}
          </div>
        </div>
      </DialogContent>
    </>
  );
};

export default ClientDetailTabBarProgressDates;
