import * as React from "react";
import theme from "./Theme";
import { ThemeProvider, Alert, AlertTitle } from "@mui/material";
import Login from "./auth/Login";
import Register from "./auth/Register";
import Home from "./Home";
import {
  BrowserRouter,
  Routes,
  Route,
  Navigate,
  redirect,
} from "react-router-dom";
import PotentialClientTrackerApi from "./api";

function App() {
  const [clients, setClients] = React.useState([]);
  const [resStatus, setResStatus] = React.useState(null);
  const [userId, setUserId] = React.useState(null);
  // const [err, setErr] = React.useState(null);
  React.useEffect(() => {
    const fetchClients = async () => {
      try {
        const clients = await PotentialClientTrackerApi.getAllClients();
        setClients(clients);
      } catch (e) {
        setResStatus(e.response.status);
        // setErr(e.response.statusText);
      }
    };
    fetchClients();
  }, [resStatus]);
  // console.log(err);

  const handleLogin = (id) => {
    setUserId(id);
    setResStatus(200);
  };

  return (
    <ThemeProvider theme={theme}>
      <div className="App">
        <BrowserRouter>
          <Routes>
            <Route path="/register" element={<Register />} />
            <Route
              path="/login"
              element={<Login handleLogin={handleLogin} />}
            />
            <Route
              path="/"
              element={
                resStatus === 200 ? <Home /> : <Navigate to="/login" replace />
              }
            />
          </Routes>
        </BrowserRouter>
      </div>
    </ThemeProvider>
  );
}

export default App;
