import * as React from "react";
import "./progress.css";
import { status } from "../../../helper/formSelectOptions";
import { DialogTitle, DialogContent, Chip, Divider } from "@mui/material";

import CloseIcon from "@mui/icons-material/Close";
import moment from "moment";
import $ from "jquery";
import PotentialClientTrackerApi from "../../../api";
import ClientDetailTabBarProgressAddDatesForm from "./ClientDetailTabBarProgressAddDatesForm";

const ClientDetailTabBarProgressDates = ({ statusMapping, client }) => {
  const [clientDetails, setClientDetails] = React.useState(client);
  const [dummy, setDummy] = React.useState(true);
  const [openAdd, setOpenAdd] = React.useState(false);
  const [openEdit, setOpenEdit] = React.useState(false);
  const [statusId, setStatusId] = React.useState(null);

  const handleClickOpen = (e) => {
    let idStatus;
    if (!$(e.target)[0].id) {
      idStatus = $($(e.target)[0].parentElement)[0].id;
    } else {
      idStatus = $(e.target)[0].id;
    }
    const id = idStatus.split("-")[0];
    console.log($(e.target));
    // setOpenAdd(true);
    setStatusId(id);
  };

  const handleClose = () => {
    setOpenAdd(false);
  };

  React.useEffect(() => {
    $(".stepper_item").map((item, idx) => {
      if ($(idx).siblings().children().length !== 0) {
        $(idx).addClass("success");
      }
      if ($(idx).siblings().children().length === 0) {
        $(idx).removeClass("success");
      }
    });
  });

  React.useEffect(() => {
    const fetchClient = async (id) => {
      const { data } = await PotentialClientTrackerApi.getClient(id);
      setClientDetails(data.client);
    };
    fetchClient(client.id);
  }, [dummy]);

  const handleDateDelete = async (e) => {
    let id;
    if (!$(e.target)[0].id) {
      id = $($(e.target)[0].parentElement)[0].id;
    } else {
      id = $(e.target)[0].id;
    }
    await PotentialClientTrackerApi.deleteStatus(id);
    setDummy(!dummy);
  };

  const chipstyle = {
    cursor: "pointer",
    backgroundColor: "#FFCF1C",
    minWidth: "120px",
    fontSize: "0.9rem",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "10px",
    height: "32px",
    color: "rgba(0, 0, 0, 0.87)",
    borderRadius: "16px",
  };

  return (
    <>
      <DialogTitle sx={{ display: "flex", justifyContent: "space-between" }}>
        Client Progress
      </DialogTitle>
      <DialogContent>
        <div className="deal-view">
          <div className="stepper">
            {status.map((s) => (
              <>
                <div className="stepper_item_date" key={s.value}>
                  <div
                    className="stepper_item "
                    onClick={handleClickOpen}
                    id={`${s.value}-status`}
                  >
                    <div className="stepper_title">{s.label}</div>
                  </div>
                  <div className="stepper_date">
                    {clientDetails.status_updated_dates
                      ? clientDetails.status_updated_dates.map((date) => {
                          if (statusMapping[date.status_id] === s.label) {
                            return (
                              <div
                                style={chipstyle}
                                onClick={handleDateDelete}
                                id={date.id}
                                key={date.id}
                              >
                                {moment(date.update_date).format("MM-DD-YYYY")}

                                <CloseIcon
                                  fontSize="sm"
                                  sx={{ marginTop: "2px" }}
                                />
                              </div>
                            );
                          }
                        })
                      : null}
                  </div>
                </div>
                <Divider />
                <ClientDetailTabBarProgressAddDatesForm
                  open={openAdd}
                  handleClose={handleClose}
                  client={clientDetails}
                  statusId={statusId}
                  statusMapping={statusMapping}
                  setClientDetails={setClientDetails}
                />
              </>
            ))}
          </div>
        </div>
      </DialogContent>
    </>
  );
};

export default ClientDetailTabBarProgressDates;
