import * as React from "react";
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  Box,
  Grid,
  DialogContent,
  Link,
  Slide,
  AppBar,
  Toolbar,
  IconButton,
  Typography,
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import ClientFormField from "./ClientFormField";

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

export default function ClientDetail({ open, handleCancel, client }) {
  const [isDisabled, setIsDisabled] = React.useState(true);

  const handleEdit = () => {
    setIsDisabled(!isDisabled);
  };

  const handleSubmit = (event) => {
    console.log(event.target);
    console.log(event.target[0].name);
    console.log(event.target[0].value);
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    // const editClientFormData = {
    //   firstName: data.get("firstName"),
    // };
    const editClientFormData = event.currentTarget.reduce((acc, input) => {
      let { name, value } = input;
      return { ...acc, [name]: value };
    }, {});
    console.log(editClientFormData);
  };

  const editFormButtons = {
    position: "absolute",
    bottom: "0",
    right: "10px",
    background: "#fafafa",
    width: "100%",
  };
  return (
    <Dialog
      fullScreen
      open={open}
      onClose={handleCancel}
      TransitionComponent={Transition}
      // maxWidth={"md"}
      // fullWidth
    >
      <AppBar sx={{ position: "relative" }}>
        <Toolbar>
          <Typography sx={{ ml: 2, flex: 1 }} variant="h6" component="div">
            Sound
          </Typography>
          <IconButton
            edge="start"
            color="inherit"
            onClick={handleCancel}
            aria-label="close"
          >
            <CloseIcon />
          </IconButton>
        </Toolbar>
      </AppBar>
      <DialogTitle>Client Information</DialogTitle>
      <DialogContent>
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
          <Grid container>
            <ClientFormField client={client} disabled={isDisabled} />
          </Grid>
          <DialogActions sx={editFormButtons}>
            {isDisabled ? (
              <>
                <Button onClick={handleCancel}>Close</Button>
                <Button onClick={handleEdit}>Edit</Button>
              </>
            ) : (
              <>
                <Button onClick={handleEdit}>Cancel</Button>
                <Button type="submit">Submit</Button>
              </>
            )}
          </DialogActions>
        </Box>
      </DialogContent>
    </Dialog>
  );
}
